let num1=document.getElementById("num1"),num2=document.getElementById("num2"),btn_sumar=document.getElementById("btn_sumar"),btn_borrar=document.getElementById("btn_borrar"),resultado=document.getElementById("resultado");btn_sumar.addEventListener("click",(function(e){e.preventDefault();let n=parseFloat(num1.value),t=parseFloat(num2.value);if(isNaN(n)||isNaN(t))resultado.innerHTML="Ingresar solo n√∫meros";else{let e=n+t;resultado.innerHTML=e}})),btn_borrar.addEventListener("click",(function(e){e.preventDefault(),resultado.innerHTML=0,num1.value=0,num2.value=0})),window.onload=function(){resultado.innerHTML=0,num1.value=0,num2.value=0};